import React from "react"
import { connect } from "react-redux"
import { Howl } from "howler"
import { sendSlotInfoToStore } from "../../../redux/instrument-preset/isntrument.action"

import "./slot.styles.scss"

class Slot extends React.Component {
  constructor(props) {
    super(props)
    this.slotNameInStore = `${this.props.name}${this.props.number}`
    this.state = {
      loaded: false,
    }
  }
  componentDidMount() {}
  componentDidUpdate() {
    let {
      number,
      time: { time },
    } = this.props

    if (
      number === time &&
      this.props.preset.preset[this.slotNameInStore].loaded
    ) {
      this.playSound()
    }

    // this.props.sendSlotInfoToStore(this.state)
    // console.log(this.props)
  }

  playSound = () => {
    const src = this.props.instrument
    let audioEl = new Howl({
      src,
    })
    if (src.includes("/ho")) {
      audioEl.volume(0.7)
    } else if (src.includes("/hh")) {
      audioEl.volume(0.5)
    }
    audioEl.play()
  }
  loadSlotWithSound = (e) => {
    const checkStoreForSlotInfo = () => {
      if (!this.props.preset.preset[this.slotNameInStore]) {
        console.log("this slot is not in store")
      }
    }
    checkStoreForSlotInfo()
    // const slotInfo = {
    //   loaded: !this.props.preset.preset[this.slotNameInStore].loaded,
    //   instrument: this.props.name,
    //   number: this.props.number,
    // }
    // this.props.sendSlotInfoToStore(slotInfo)
  }

  render() {
    return (
      <div
        onClick={(e) => this.loadSlotWithSound(e)}
        className={`instrumentBoard__slotRow__slot ${
          this.props.number === this.props.time.time
            ? "instrumentBoard__slotRow__slot__active active"
            : ""
        }
        `}
      ></div>
    )
  }
}
//  ${
//    this.state.loaded
//      ? "instrumentBoard__slotRow__slot__loaded loaded"
//      : ""
//  }

const mapStateToProps = (state) => ({
  time: state.time,
  preset: state.preset,
})
const mapDispatchToProps = (dispatch) => ({
  sendSlotInfoToStore: (slotInfo) => dispatch(sendSlotInfoToStore(slotInfo)),
})
export default connect(mapStateToProps, mapDispatchToProps)(Slot)
