import db from "../../../sounds/bd.mp3"
import hh from "../../../sounds/hh.mp3"

export const importSound = (instrument) => {
  const instrumentArray = { [db]: db, [hh]: hh }
  //   if (instrument == "hh") {
  //     return instrumentArray[hh]
  //   }
  console.log(instrument == "bd")
  if (instrument == "bd") {
    return instrumentArray[bd]
  } else if (instrument == "hh") {
    return instrumentArray[hh]
  }
}
