import bd from "../../../sounds/bd.mp3"
import hh from "../../../sounds/hh.mp3"

const instrumentArray = { [bd]: bd, [hh]: hh }
export const importSound = (instrument) => {
  if (instrument === "hh") {
    activeInstrument = instrumentArray[hh]
  }
  return instrumentArray[hh]
}
