import React from "react"
import "./preset.styles.scss"

import {
  converCollectionsSnapshotToMap,
  firestore,
} from "../../firebase/firebase"

class PresetMenu extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      presets: [],
    }
  }

  componentDidMount() {
    const collectionRef = firestore.collection("presets")
    collectionRef.onSnapshot(async (snapshot) => {
      const presetsFromDb = converCollectionsSnapshotToMap(snapshot)

      this.setState(() => {
        return { presets: presetsFromDb }
      })
    })
  }

  componentDidUpdate() {
    renderPresets = () => {
      this.state.presets.forEach((preset, index) => {
        return (
          <div class="presets__list">
            <div>
              ${preset} ${index + 1}
            </div>
          </div>
        )
      })
    }
  }
  render() {
    return (
      <div className="preset">
        Presets
        <div></div>
      </div>
    )
  }
}

export default PresetMenu
