import React from "react"
import { connect } from "react-redux"
import { Howl, Howler } from "howler"
import "./slot.styles.scss"

class Slot extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      loaded: false,
      slotInfo: {
        instrument: this.props.instrument,
        number: this.props.number,
      },
    }
  }
  componentDidMount() {}
  componentDidUpdate() {
    let {
      number,
      time: { time },
    } = this.props

    // if slotnumber === current time && has been clicked ( is loaded)
    // we play the sounds
    if (number === time && this.state.loaded) {
      this.playSound()
    }
  }

  playSound = () => {
    const src = this.props.instrument
    let audioEl = new Howl({
      src,
    })
    if (src.includes("/ho")) {
      audioEl.volume(0.7)
    } else if (src.includes("/hh")) {
      audioEl.volume(0.5)
    }
    audioEl.play()
  }
  loadSlotWithSound = (e) => {
    this.setState((state) => {
      return { loaded: !state.loaded }
    })
    console.log(this.state)
  }

  render() {
    return (
      <div
        onClick={(e) => this.loadSlotWithSound(e)}
        className={`instrumentBoard__slotRow__slot ${
          this.props.number === this.props.time.time
            ? "instrumentBoard__slotRow__slot__active active"
            : ""
        } ${
          this.state.loaded
            ? "instrumentBoard__slotRow__slot__loaded loaded"
            : ""
        }`}
      ></div>
    )
  }
}

const mapStateToProps = (state) => ({
  time: state.time,
})
export default connect(mapStateToProps)(Slot)
